import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "../styles/profile.css";

function Profile() {
  const { user, logout } = useContext(AuthContext);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user || !user.token) {
      navigate("/login");
      return;
    }

    try {
      const decodedToken = parseJwt(user.token);
      setUserData(decodedToken);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –¥–µ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–æ–∫–µ–Ω–∞:", error);
      logout();
      navigate("/login");
    }
  }, [user, navigate, logout]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img
            className="cover-photo"
            src="https://source.unsplash.com/800x300/?nature,water"
            alt="–û–±–ª–æ–∂–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è"
          />
          <img
            className="avatar"
            src="https://source.unsplash.com/150x150/?person,face"
            alt="–ê–≤–∞—Ç–∞—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è"
          />
        </div>
        <div className="profile-info">
          <h2>{userData?.Username || "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å"}</h2>
          <p><strong>üìß Email:</strong> {userData?.email}</p>
          <p><strong>üÜî ID:</strong> {userData?.user_id}</p>
          <div className="profile-actions">
            <button className="edit-btn">‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å</button>
            <button className="logout-btn" onClick={handleLogout}>üö™ –í—ã–π—Ç–∏</button>
          </div>
        </div>
      </div>
    </div>
  );
}

function parseJwt(token) {
  try {
    const base64Url = token.split(".")[1];
    const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
    return JSON.parse(atob(base64));
  } catch (error) {
    console.error("–û—à–∏–±–∫–∞ –¥–µ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–æ–∫–µ–Ω–∞:", error);
    return null;
  }
}

export default Profile;
