import { useState, useEffect } from "react";
import axios from "axios";
import { createGoal, getAllGoals, deleteGoal } from "../services/goalsService";


export default function GoalManager() {
  const [goals, setGoals] = useState([]);
  const [newGoal, setNewGoal] = useState("");
  const [editingGoal, setEditingGoal] = useState(null);
  const [editedText, setEditedText] = useState("");

  useEffect(() => {
    fetchGoals();
  }, []);

  const fetchGoals = async () => {
    try {
      const response = await axios.get(API_URL);
      setGoals(response.data);
    } catch (error) {
      console.error("Ошибка при загрузке целей", error);
    }
  };

  const createGoal = async () => {
    if (!newGoal.trim()) return;
    try {
      const response = await axios.post(API_URL, { title: newGoal });
      setGoals([...goals, response.data]);
      setNewGoal("");
    } catch (error) {
      console.error("Ошибка при создании цели", error);
    }
  };

  const deleteGoal = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      setGoals(goals.filter((goal) => goal._id !== id));
    } catch (error) {
      console.error("Ошибка при удалении цели", error);
    }
  };

  const updateGoal = async () => {
    if (!editedText.trim() || !editingGoal) return;
    try {
      await axios.put(`${API_URL}/${editingGoal._id}`, { title: editedText });
      setGoals(goals.map((goal) => (goal._id === editingGoal._id ? { ...goal, title: editedText } : goal)));
      setEditingGoal(null);
      setEditedText("");
    } catch (error) {
      console.error("Ошибка при обновлении цели", error);
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-gray-100 rounded-lg shadow-lg mt-10">
      <h1 className="text-2xl font-bold text-center mb-4">Goal Manager</h1>
      <div className="flex mb-4">
        <input
          type="text"
          className="flex-1 p-2 border rounded-lg"
          placeholder="Введите новую цель"
          value={newGoal}
          onChange={(e) => setNewGoal(e.target.value)}
        />
        <button onClick={createGoal} className="ml-2 bg-blue-500 text-white px-4 py-2 rounded-lg">
          Добавить
        </button>
      </div>

      <ul>
        {goals.map((goal) => (
          <li key={goal._id} className="flex justify-between items-center bg-white p-3 mb-2 rounded-lg shadow">
            {editingGoal && editingGoal._id === goal._id ? (
              <input
                type="text"
                value={editedText}
                onChange={(e) => setEditedText(e.target.value)}
                className="p-1 border rounded-lg"
              />
            ) : (
              <span>{goal.title}</span>
            )}
            <div>
              {editingGoal && editingGoal._id === goal._id ? (
                <button onClick={updateGoal} className="bg-green-500 text-white px-3 py-1 rounded-lg ml-2">
                  Сохранить
                </button>
              ) : (
                <button onClick={() => { setEditingGoal(goal); setEditedText(goal.title); }} className="bg-yellow-500 text-white px-3 py-1 rounded-lg ml-2">
                  Редактировать
                </button>
              )}
              <button onClick={() => deleteGoal(goal._id)} className="bg-red-500 text-white px-3 py-1 rounded-lg ml-2">
                Удалить
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
