import React, { useState } from "react";

const SendOTP = ({ setStep, setEmail }) => {
  const [localEmail, setLocalEmail] = useState("");

  const handleSendOTP = async () => {
    try {
      console.log(`Отправка OTP на ${localEmail}`);
      setEmail(localEmail); // Передаем email в родительский компонент
      setStep(2); // Переход на следующий шаг
    } catch (error) {
      console.error("Ошибка отправки OTP:", error);
    }
  };

  return (
    <div>
      <h2>Введите ваш email для получения OTP</h2>
      <input
        type="email"
        placeholder="Email"
        value={localEmail}
        onChange={(e) => setLocalEmail(e.target.value)}
      />
      <button onClick={handleSendOTP}>Отправить OTP</button>
    </div>
  );
};

export default SendOTP;
